definition.script:
pipeline {
    agent { node 'aws-ec2-cloud' }
    environment {
        registry_url = ""
    }
    stages {
        stage ('[i] Clean WC') {
            steps {
                cleanWs()
            }
        }
        stage ('[i] GIT') {
            steps {
                git branch: 'dev-jenkins', credentialsId: 'github', url: 'https://github.com/publius-github/ci-cd.git'
            }
        }
        stage('[i] Docker build [TODO]') {
            steps {
                    sh 'cd ${WORKSPACE}'
                    sh 'docker build -f "app-examples/WeatherForecast/Dockerfile" -t 803808824931.dkr.ecr.us-east-1.amazonaws.com/weathercast:latest .'
                }
        }
        stage('[i] Docker push [TODO]') {
            steps {
                script {
                    docker.withRegistry("https://803808824931.dkr.ecr.us-east-1.amazonaws.com/weathercast", "ecr:us-east-1:aws") {
                    docker.image("803808824931.dkr.ecr.us-east-1.amazonaws.com/weathercast:latest").push()
                    }
                }
            }
        }

        stage('[i] Remove tmp docker files') {
            steps {
                    sh 'docker system prune -a -f'
            }
        }
    }
}
